import {
  AppBar,
  Button,
  Toolbar,
  Menu,
  MenuItem,
  Fade,
  Typography,
} from '@material-ui/core';
import MuiGrid from '@material-ui/core/Grid';
import { connect } from 'react-redux';
import { IRootState } from '../../Store/GlobalStore';
import store from '../../Store/GlobalStore';
import styled from '@material-ui/styles/styled';
import { useHistory, withRouter, RouteComponentProps } from 'react-router-dom';
import { LogoutUser } from '../../API/Login/loginAuth';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import {
  compose as composeStyles,
  palette,
  sizing,
  spacing,
} from '@material-ui/system';
import React, { useState } from 'react';
import { useIntl } from 'react-intl';
import Styles from './Style';
const Grid = styled(MuiGrid)(composeStyles(spacing, palette, sizing));

interface Props extends RouteComponentProps<any> {
  isAuthenticated: Boolean;
}

const Header: React.FC<Props> = ({ isAuthenticated }) => {
  const intl = useIntl();
  const classes = Styles();
  const history = useHistory();
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Grid container className={classes.root}>
      <AppBar position="sticky" className={classes.container}>
        <Toolbar>
          <Typography
            variant="h6"
            align="center"
            className={classes.title}
            onClick={() => history.push('/')}
          >
            {intl.formatMessage({ id: 'pet_link' })}
          </Typography>

          {isAuthenticated ? (
            <React.Fragment>
              <Button
                aria-controls="fade-menu"
                aria-haspopup="true"
                onClick={handleClick}
                color="inherit"
              >
                <AccountCircleIcon />
              </Button>
              <Menu
                id="fade-menu"
                anchorEl={anchorEl}
                getContentAnchorEl={null}
                keepMounted
                open={open}
                onClose={handleClose}
                TransitionComponent={Fade}
                anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                transformOrigin={{ vertical: 'top', horizontal: 'center' }}
              >
                <MenuItem onClick={handleClose}>
                  {intl.formatMessage({ id: 'profile' })}
                </MenuItem>
                <MenuItem onClick={handleClose}>
                  {intl.formatMessage({ id: 'my_account' })}
                </MenuItem>
                <MenuItem
                  onClick={() => {
                    setAnchorEl(null);
                    store.dispatch(LogoutUser());
                  }}
                >
                  {intl.formatMessage({ id: 'logout' })}
                </MenuItem>
              </Menu>
            </React.Fragment>
          ) : (
            <Button
              color="inherit"
              type="button"
              onClick={() => history.push('/login')}
            >
              <Typography align="right">
                {intl.formatMessage({ id: 'login' })}
              </Typography>
            </Button>
          )}
        </Toolbar>
      </AppBar>
    </Grid>
  );
};

const mapProps = (state: IRootState) => {
  return {
    isAuthenticated: state.loginStore.isAuthenticated,
  };
};

export default withRouter(connect(mapProps)(Header));
