import React, { useState } from 'react';
import { Form, Formik, Field } from 'formik';
import MuiGrid from '@material-ui/core/Grid';
import {
  Paper,
  CardActions,
  CardContent,
  CardMedia,
  Card,
} from '@material-ui/core';
import Styles from './Styles';
import {
  CARD_ITEMS,
  CATEGORY_ITEMS,
  CATEGORY_ITEMS_TWO,
  CATEGORY_ITEMS_THREE,
} from './Constants';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import styled from '@material-ui/styles/styled';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { useIntl } from 'react-intl';
import Box from '@material-ui/core/Box';
import {
  compose as composeStyles,
  palette,
  sizing,
  spacing,
} from '@material-ui/system';
import { MyTextField } from '../Common/TextField';
const Grid = styled(MuiGrid)(composeStyles(spacing, palette, sizing));

interface Values {
  userName: string;
  password: string;
  emailAddress: string;
}

interface Props {
  onSubmit: (values: Values) => void;
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: any;
  value: any;
}

function Contents(props: Props) {
  const classes = Styles();
  const [value, setValue] = useState(0);
  const intl = useIntl();
  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
      <Grid hidden={value !== index} id={`simple-tabpanel-${index}`} {...other}>
        {value === index && <Box>{children}</Box>}
      </Grid>
    );
  }
  function a11yProps(index: any) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  const onSubmit = (values: object) => {};

  return (
    <React.Fragment>
      <Grid px={4} container className={classes.carouselStyles}>
        <Grid item md={7} py={8} px={5}>
          <Typography variant="h3">
            {intl.formatMessage({ id: 'pet_link' })}
          </Typography>
        </Grid>
        <Grid item md={4} py={8} px={5}>
          <Paper style={{ padding: '16px' }}>
            <Formik
              initialValues={{ password: '', emailAddress: '', userName: '' }}
              onSubmit={(values) => {
                onSubmit(values);
              }}
            >
              {({ values, handleChange, handleBlur }) => (
                <Form>
                  <Grid pb={2}>
                    <Field
                      component={MyTextField}
                      name="username"
                      label={intl.formatMessage({ id: 'username' })}
                      placeholder={intl.formatMessage({ id: 'username' })}
                      size="small"
                    />
                  </Grid>
                  <Grid pb={2}>
                    <Field
                      component={MyTextField}
                      name="emailAddress"
