import React, { useState } from 'react';
import Carousel from 'react-material-ui-carousel';
import MuiGrid from '@material-ui/core/Grid';
import { Paper } from '@material-ui/core';
import Styles from './Styles';
import {
  CAROUSEL_ITEMS,
  CARD_ITEMS,
  CATEGORY_ITEMS,
  CATEGORY_ITEMS_TWO,
  CATEGORY_ITEMS_THREE,
} from './Constants';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import styled from '@material-ui/styles/styled';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import { useIntl } from 'react-intl';
import Box from '@material-ui/core/Box';
import {
  compose as composeStyles,
  palette,
  sizing,
  spacing,
} from '@material-ui/system';
import Nana from '../../assets/images/nana.jpeg';
const Grid = styled(MuiGrid)(composeStyles(spacing, palette, sizing));

interface Props {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
}

interface TabPanelProps {
  children?: React.ReactNode;
  index: any;
  value: any;
}

function Contents(props: Props) {
  const classes = Styles();
  const [value, setValue] = useState(0);
  const intl = useIntl();
  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
      <Grid hidden={value !== index} id={`simple-tabpanel-${index}`} {...other}>
        {value === index && <Box>{children}</Box>}
      </Grid>
    );
  }
  function a11yProps(index: any) {
    return {
      id: `simple-tab-${index}`,
      'aria-controls': `simple-tabpanel-${index}`,
    };
  }

  return (
    <React.Fragment>
      <Carousel indicators={false} navButtonsAlwaysVisible={true}>
        {CAROUSEL_ITEMS.map((item) => (
          <Paper variant="outlined" square className={classes.carouselStyles}>
            <img className={classes.images} src={item.imgSrc} />
          </Paper>
        ))}
      </Carousel>
      <Grid container px={5} py={3} spacing={4}>
        {CARD_ITEMS.map((item) => (
          <Grid item md={4}>
            <Card className={classes.paperImg}>
              <CardMedia
                component="img"
                alt="Contemplative Reptile"
                height="140"
                image={item.imgSrc}
                title="Contemplative Reptile"
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {intl.formatMessage({ id: item.title })}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {intl.formatMessage({ id: item.description })}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small">
                  {intl.formatMessage({ id: 'find_out_more' })}
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Grid container px={5} py={3}>
        <Grid item md={12}>
          <Typography variant="h6" align="center">
            {intl.formatMessage({ id: 'shop_by_category' })}
          </Typography>
          <Grid
            item
            md={12}
            py={3}
            justify="center"
            className={classes.categoryClass}
          >
            <Tabs
              value={value}
              onChange={handleChange}
              aria-label="simple tabs example"
            >
              <Tab label="Accessories" {...a11yProps(0)} />
              <Tab label="Foods" {...a11yProps(1)} />
              <Tab label="Vitamins" {...a11yProps(2)} />
            </Tabs>
          </Grid>
        </Grid>
        <TabPanel value={value} index={0}>
          <Grid container spacing={3} px={5}>
            {CATEGORY_ITEMS.map((item) => (
              <Grid item md={3}>
                <img className={classes.images} src={item.imgSrc} />
              </Grid>
            ))}
          </Grid>
        </TabPanel>
        <TabPanel value={value} index={1}>
          <Grid container spacing={3} px={5}>
            {CATEGORY_ITEMS_TWO.map((item) => (
              <Grid item md={3}>
                <img className={classes.images} src={item.imgSrc} />
              </Grid>
            ))}
          </Grid>
        </TabPanel>
        <TabPanel value={value} index={2}>
          <Grid container spacing={3} px={5}>
            {CATEGORY_ITEMS_THREE.map((item) => (
              <Grid item md={3}>
                <img className={classes.images} src={item.imgSrc} />
              </Grid>
            ))}
          </Grid>
        </TabPanel>
      </Grid>
    </React.Fragment>
  );
}

export default Contents;
