import { Errors } from '../Types.ts/LoginTypes';
import { createModel } from '@rematch/core';

export const loginStore = createModel({
  state: [],
  reducers: {
    errors: (state: State) => state,
    setErrors: (state: State, payload: Errors) => ({
      ...state,
      payload,
    }),
  },
});
