import { Errors, RegistrationErrors } from '../Types.ts/LoginTypes';
import { createModel } from '@rematch/core';
import { isEmpty } from 'lodash';

interface State {
  errors: Errors;
  registrationErrors: RegistrationErrors;
}

interface RegistrationState {
  registrationErrors: RegistrationErrors;
}

interface LoginAuthentication {
  user: {};
  isAuthenticated: boolean;
}

const initialState: State = {
  errors: {
    email: '',
    password: '',
  },
  registrationErrors: {
    userName: '',
    email: '',
    password: '',
  },
  isAuthenticated: false,
  user: {},
};

export const loginStore = createModel({
  state: initialState,
  reducers: {
    user: (state: LoginAuthentication) => state,
    setCurrentUser: (
      state: LoginAuthentication,
      payload: LoginAuthentication
    ) => ({
      ...state,
      isAuthenticated: !isEmpty(payload),
      user: payload,
    }),
    errors: (state: State) => state,
    registrationErrors: (state: RegistrationState) => state,
    setErrors: (state: State, payload: Errors) => ({
      ...state,
      errors: payload,
    }),
    setRegistrationErrors: (
      state: RegistrationState,
      payload: RegistrationErrors
    ) => ({
      ...state,
      registrationErrors: payload,
    }),
  },
});
