import React, { useState } from 'react';
import LoginForm from '../Components/LoginForm';
import PropTypes from 'prop-types';
import { loginUser } from '../../../API/Login/loginAuth';
import { connect } from 'react-redux';
import { Dispatch } from 'redux';

interface Values {
  emailAddress: string;
  password: string;
}

interface Props {
  loginUser: (values: Values) => void;
}

const Login: React.FC<Props> = ({ loginUser }) => {
  const [isLoading, setIsLoading] = useState<Boolean>(false);
  const onSubmit = (values: Values) => {
    setIsLoading(true);
    const userData = {
      emailAddress: values.emailAddress,
      password: values.password,
    };

    loginUser(userData);
  };

  return (
    <LoginForm
      isLoading={isLoading}
      onSubmit={({ emailAddress, password }) =>
        onSubmit({ emailAddress, password })
      }
    />
  );
};

export default connect((dispatch, ownProps) => ({
  onDelete: () => dispatch(deleteItem(ownProps.id)),
}))(Login);
