import axios from 'axios';
import { RematchDispatch } from '@rematch/core';
import setAuthToken from '../../Utils/setAuthToken';
import jwt_decode from 'jwt-decode';
import { dispatch } from '../../Store/GlobalStore';

interface Credentials {
  emailAddress: string;
  password: string;
}

interface Registration {
  userName: string;
  emailAddress: string;
  password: string;
}

export const loginUser = (userData: Credentials) => {
  axios
    .post('/api/users/sign-in', userData)
    .then((res) => {
      // const { token } = res.data;
      // localStorage.setItem('jwtToken', token);
      // setAuthToken(token);
      // const decoded = jwt_decode(token);
      // // Set current user
      // dispatch(setCurrentUser(decoded));
    })
    .catch((err) => {
      dispatch.loginStore.setErrors(err.response.data);
    });
};

export const registerUser = (userData: Registration) => {
  axios
    .post('/api/users/register', userData)
    .then((res) => {})
    .catch((err) => {});
};
