import React, { useState, useEffect } from 'react';
import LoginForm from '../Components/LoginForm';
import { loginUser } from '../../../API/Login/loginAuth';
import { connect } from 'react-redux';
import { IRootState, Dispatch } from '../../../Store/GlobalStore';

interface Values {
  emailAddress: string;
  password: string;
}

interface Props {
  errors: object;
}

const Login: React.FC<Props> = ({ errors }) => {
  const [isLoading, setIsLoading] = useState<Boolean>(false);
  const onSubmit = (values: Values) => {
    setIsLoading(true);
    const userData = {
      emailAddress: values.emailAddress,
      password: values.password,
    };

    loginUser(userData);
  };

  console.log(errors);

  return (
    <LoginForm
      isLoading={isLoading}
      onSubmit={({ emailAddress, password }) =>
        onSubmit({ emailAddress, password })
      }
    />
  );
};

const mapProps = (state: IRootState) => ({
  errors: state.loginStore.errors,
});

const mapDispatch = (dispatch: Dispatch) => ({
  errors: dispatch.loginStore.setErrors,
});

export default connect(mapProps, mapDispatch as any)(Login);
