import { Errors } from '../Types.ts/LoginTypes';
import { createModel } from '@rematch/core';

type State = Array<Errors>;

const initialState = {
  errors: [],
};

export const loginStore = createModel({
  state: initialState,
  reducers: {
    errors: (state: State, payload: Errors) => payload,
  },
});
